import axios from "axios";
export default {
  name: "Search",
  data: function data() {
    return {
      keyWord: ""
    };
  },
  methods: {
    searchUsers: function searchUsers() {
      var _this = this;

      /*请求前更新List的数据*/
      this.$bus.$emit('updateListData', {
        isFirst: false,
        users: [],
        isLoading: true,
        errMsg: ''
      });
      axios.get("http://api.github.com/search/users?q=".concat(this.keyWord)).then(function (response) {
        /*请求成功后更新List的数据*/
        console.log('请求成功', response.data.items);

        _this.$bus.$emit('updateListData', {
          isFirst: false,
          users: response.data.items,
          isLoading: false,
          errMsg: ''
        });
      }, function (error) {
        /*请求失败后更新List的数据*/
        console.log('请求失败', error.message);

        _this.$bus.$emit('updateListData', {
          isFirst: false,
          users: [],
          isLoading: false,
          errMsg: error.message
        });
      });
    }
  },
  mounted: function mounted() {}
};
