/*该文件专门创建整个应用的路由器*/
import VueRouter from 'vue-router'
import Home from "@/pages/Home";
import About from "@/pages/About";
import News from "@/pages/News";
import Message from "@/pages/Message";
import Detail from "@/pages/Detail";

/*创建并暴露一个路由器*/
const router = new VueRouter({
    routes: [
        {
            name: 'guanyu',
            path: '/about',
            component: About,
        },
        {
            name: 'zhuye',
            path: '/home',
            component: Home,
            children: [
                {
                    name: 'xinwen',
                    path: 'news',
                    component: News,
                    meta: {
                        isAuth: true
                    }
                },
                {
                    name: 'xiaoxi',
                    path: 'message',
                    component: Message,
                    meta: {
                        isAuth: true
                    },
                    children: [
                        {
                            name: 'xiangqing',
                            path: 'detail',
                            component: Detail,
                            /*
                            props的第一种写法,值为对象,该对象中的所有key-value都会以props的形式传给Detail组件
                            props:{a:1,b:'hello'}*/

                            /*
                            props的第二种写法,值为布尔值,若布尔值为真,就会把该路由组件收到的所有params参数以
                            以props的形式传给Detail组件
                            props:true*/

                            /*props的第三种写法,值为函数*/
                            props: function ($route) {
                                return {
                                    id: $route.query.id,
                                    title: $route.query.title
                                }
                            }
                        }
                    ]
                }
            ]
        },
    ]
})

/*全局前置路由守卫——————初始化的时候被调用、每次路由切换之前被调用*/
router.beforeEach((to, from, next) => {
    console.log(to, from)
    if (to.meta.isAuth) {
        if (localStorage.getItem('school') === 'guigu') {
            next()
        } else {
            alert('学校名称不一致,无权限查看')
        }
    } else {
        next()
    }


})

export default router
