import MyHeader from "@/components/MyHeader";
import MyList from "@/components/MyList";
import MyFooter from "@/components/MyFooter";
export default {
  name: 'App',
  components: {
    MyHeader: MyHeader,
    MyList: MyList,
    MyFooter: MyFooter
  },
  data: function data() {
    return {
      /*由于todos是MyHeader组件和MyFooter组件都在使用,所以放在App中(状态提升)*/
      todos: [{
        id: '001',
        title: '抽烟',
        done: true
      }, {
        id: '002',
        title: '喝酒',
        done: false
      }, {
        id: '003',
        title: '开车',
        done: true
      }]
    };
  },
  methods: {
    /*添加一个todo*/
    addTodo: function addTodo(todoObj) {
      this.todos.unshift(todoObj);
    },

    /*勾选or取消勾选一个todo*/
    checkTodo: function checkTodo(id) {
      this.todos.forEach(function (todo) {
        if (todo.id === id) {
          todo.done = !todo.done;
        }
      });
    },

    /*删除一个todo*/
    deleteTodo: function deleteTodo(id) {
      this.todos = this.todos.filter(function (todo) {
        return todo.id !== id;
      });
    },

    /*全选or取消全选*/
    checkAllTodo: function checkAllTodo(done) {
      this.todos.forEach(function (todo) {
        todo.done = done;
      });
    },

    /*清楚所有已经完成的todo*/
    clearAllTodo: function clearAllTodo() {
      this.todos = this.todos.filter(function (todo) {
        return !todo.done;
      });
    }
  }
};
