import axios from "axios";
export default {
  name: "Search",
  data: function data() {
    return {
      keyWord: ""
    };
  },
  methods: {
    searchUsers: function searchUsers() {
      var _this = this;

      /*请求前更新List的数据*/
      this.$bus.$emit('updateListData', false, true, '', []);
      axios.get("http://api.github.com/search/users?q=".concat(this.keyWord)).then(function (response) {
        console.log('请求成功', response.data.items);

        _this.$bus.$emit('updateListData', response.data.items);
      }, function (error) {
        console.log('请求失败', response.message);
      });
    }
  },
  mounted: function mounted() {}
};
