/*该文件专门创建整个应用的路由器*/
import VueRouter from 'vue-router'
import Home from "@/pages/Home";
import About from "@/pages/About";
import News from "@/pages/News";
import Message from "@/pages/Message";
import Detail from "@/pages/Detail";

/*创建并暴露一个路由器*/
export default new VueRouter({
    routes: [
        {
            name:'guanyu',
            path: '/about',
            component: About
        },
        {
            path: '/home',
            component: Home,
            children: [
                {
                    path: 'news',
                    component: News
                },
                {
                    path: 'message',
                    component: Message,
                    children:[
                        {
                            name:'xiangqing',
                            path:'detail/:id/:title',
                            component:Detail,
                            /*props的第一种写法,值为对象,该对象中的所有key-value都会以*/
                            props:{a:1,b:'hello'}
                        }
                    ]
                }
            ]
        },

    ]
})
