import { mapState } from 'vuex';
export default {
  name: "Count",
  data: function data() {
    return {
      /*当前用户选择的数字*/
      num: 1
    };
  },
  methods: {
    increment: function increment() {
      this.$store.commit("JIA", this.num);
    },
    decrement: function decrement() {
      this.$store.commit("JIAN", this.num);
    },
    incrementOdd: function incrementOdd() {
      this.$store.dispatch('jiaOdd', this.num);
    },
    incrementWait: function incrementWait() {
      this.$store.dispatch('jiaWait', this.num);
    }
  },
  computed: {
    sum: function sum() {
      return this.$store.state.sum;
    },
    bigSum: function bigSum() {
      return this.$store.getters.bigSum;
    },
    school: function school() {
      return this.$store.state.school;
    },
    subject: function subject() {
      return this.$store.state.subject;
    }
  },
  mounted: function mounted() {
    console.log('CountComponent', this);
    var x = mapState({
      'sum': sum
    });
    console.log(x);
  }
};
