export default {
  name: "About",

  mounted() {
    console.log('%%%', this.$route);
  },

  /*通过路由规则,进入该组件时被调用*/
  beforeRouteEnter(to, from, next) {
    console.log('beforeRouteEnter', to, from);

    if (to.meta.isAuth) {
      if (localStorage.getItem('school') === 'guigu') {
        next();
      } else {
        alert('学校名不对,无权限查看');
      }
    } else {
      next();
    }
  },

  /*通过路由规则,离开该组件时被调用*/
  beforeRouteLeave(to, from, next) {
    console.log('beforeRouteLeave', to, from);
    next();
  }

};
