/*该文件用于创建vuex中最为核心的store*/

/*引入Vuex*/
import Vuex from 'vuex'
import Vue from 'vue'
Vue.use(Vuex)
/*准备actions,用于响应组件中的动作*/
const actions = {
    jia:function (context,value){
        console.log('actions中的jia被调用了',context,value)
    }
}

/*准备mutations,用于操作数据*/
const mutations = {}

/*准备state,用于存储数据*/
const state = {
    /*当前的和*/
    sum:0
}

/*创建store,并导出store*/
export default new Vuex.Store({
    actions,
    mutations,
    state
})
